    public static double polaralpha(double x, double y) {
        double z = Math.sqrt(x * x + y * y);
        if ((x>0)&(y>0)) {
            return Math.acos(x / z);
        }
        else if ((x<0)&(y>0)) {
            return Math.acos(x / z);
        }
        else if ((x<0)&(y<0)) {
            return Math.acos(-x / z) + Math.PI;
        }
        else if ((x>0)&(y<0)) {
            return Math.acos(-x / z) + Math.PI;
        }
        else if ((x==0)&(y>0)) {
            return Math.PI / 2;
        }
        else if ((x==0)&(y<0)) {
            return 3 * Math.PI / 2;
        }
        else if ((x>0)&(y==0)) {
            return 0;
        }
        else if ((x<0)&(y==0)) {
            return Math.PI;
        }
        else {
            return 0;
        }
    }
    
